import styled from "styled-components/macro"

export const Title = styled.h1`
  font-size: 3.5rem;
  color: white;
  margin: 0 155px;
  margin-bottom: 1.2rem;

  @media screen and (max-width: 900px) {
    margin: 0 100px;
    font-size: 3rem;
  }
`
export const Subtitle = styled.h2`
  font-size: 1.625rem;
  margin: 16px 155px;
  margin-bottom: 2.5rem;
`
