import { HeaderContainer } from "../containers/header"
import { FooterContainer } from "../containers/footer"
import { Form } from "../components"
import { useState } from "react"

export default function Signin() {
  const [showPassword, setShowPassword] = useState(false)
  const [emailAddress, setEmailAddress] = useState("")
  const [password, setPassword] = useState("")
  const [error, setError] = useState("")

  const isInvalid = password === "" && emailAddress === ""

  const label_active = {
    top: "7px",
    fontSize: "0.6875rem",
  }

  return (
    <>
      <HeaderContainer>
        <Form.Frame>
          <Form.Inner>
            <Form.Title>Sign In</Form.Title>
            {error & <Form.Error>{error}</Form.Error>}
            {/* Email Input */}
            <Form.InputFrame type="email" value={emailAddress}>
              <Form.InputFrame>
                <Form.Input onChange={(e) => setEmailAddress(e.target.value)} />
                <Form.Label
                  style={emailAddress.length > 0 ? label_active : null}
                >
                  Email or phone number
                </Form.Label>
              </Form.InputFrame>
            </Form.InputFrame>
            {/* Password Input */}
            <Form.InputFrame
              type="password"
              value={password}
              onChange={({ target }) => setPassword(target.value)}
            >
              <Form.Input
                onChange={(e) => setPasswordValue(e.target.value)}
                type={showPassword ? "password" : "word"}
              />
              <Form.Label style={password.length > 0 ? label_active : null}>
                Password
              </Form.Label>
              <Form.ShowButton
                type="button"
                onClick={() => setShowPassword((showPassword) => !showPassword)}
              >
                Show
              </Form.ShowButton>
            </Form.InputFrame>
            <Form.Button disabled={isInvalid} type="submit">
              Sign In
            </Form.Button>
          </Form.Inner>
        </Form.Frame>
      </HeaderContainer>
    </>
  )
}
