import { Header } from "../components"

export function SelectProfileContainer({ user, setProfile }) {
  return (
    <Header>
      <Header.Inner>
        <Header.Logo
          src={"/images/icons/logo.svg"}
          alt="Nerflix"
          to={ROUTES.HOME}
        />

        {location.pathname === ROUTES.HOME ? (
          <Header.ButtonLink to={ROUTES.SIGN_IN}>Sign In</Header.ButtonLink>
        ) : null}
      </Header.Inner>
      {children}
    </Header>
  )
}
