import { useState } from "react"
import {
  Container,
  Frame,
  Inner,
  Error,
  Title,
  InputFrame,
  Label,
  ShowButton,
  Input,
  Button,
  SignupButton,
  Text,
} from "./styles/form"




export default function Form({ children, ...restProps }) {
  return <Container>{children}</Container>
}

Form.Frame = function FormFrame({ children, ...restProps }) {
  return <Frame {...restProps}>{children}</Frame>
}

Form.Inner = function FormInner({ children, ...restProps }) {
  return <Inner {...restProps}>{children}</Inner>
}

Form.Error = function FormError({ children, ...restProps }) {
  return <Error {...restProps}>{children}</Error>
}

Form.Title = function FormTitle({ children, ...restProps }) {
  return <Title {...restProps}>{children}</Title>
}

Form.InputFrame = function FormInputFrame({ children, ...restProps }) {
  // Need to check if input has any value in it if it does need to make sure
  // to add the active on the input so the label stays small at the top
  // and input bg will keep the active bg color when we click on the show pass button
  const [emailValue, setEmailValue] = useState("")
  const [passwordValue, setPasswordValue] = useState("")

  // We need state to get the password input status
  const [showPassword, setShowPassword] = useState(true)

  const label_active = {
    top: "7px",
    fontSize: "0.6875rem"
  }

  return restProps.type === "email" ? (
    <InputFrame>
      <Input onChange={ e => setEmailValue(e.target.value)} />
      <Label style={emailValue.length > 0 ? label_active : null}>Email or phone number</Label>
    </InputFrame>
  ) : (
    <InputFrame>
      <Input onChange={e => setPasswordValue(e.target.value)} type={showPassword ? "password" : "word"} />
      <Label style={passwordValue.length > 0 ? label_active : null}>Password</Label>
      <ShowButton onClick={() => setShowPassword(showPassword => !showPassword)}>Show</ShowButton>
    </InputFrame>
  )
}

Form.Label = function FormLabel({ children, ...restProps }) {
  return <Label {...restProps}>{children}</Label>
}

Form.ShowButton = function FormShowButton({ children, ...restProps }) {
  return <ShowButton {...restProps}>{children}</ShowButton>
}

Form.Input = function FormInput({ ...restProps }) {
  return <Input {...restProps} />
}

Form.Button = function FormButton({ children, ...restProps }) {
  return <Button {...restProps}>{children}</Button>
}

Form.SignupButton = function FormSignupButton({ children, ...restProps }) {
  return <SignupButton {...restProps}>{children}</SignupButton>
}

Form.Text = function FormText({ children, ...restProps }) {
  return <Text {...restProps}>{children}</Text>
}
